<script>
import {mapGetters, mapMutations} from 'vuex'
import {SET_USER} from '../store/types'

const Account_menu = {
    data: ()=>({
        country: "",
    }),
    computed: {
        ...mapGetters([
            'getUser'
        ]),
    },
    mounted() {
        this.getCountry()
    },
    methods: {
        ...mapMutations({
            setUser: SET_USER
        }),
        getCountry(){
            axios.get("/countryName")
            .then(resp => {
                this.country = resp.data
            })
        },
        linkTo(){
            if(this.country != 'Ukraine'){
                this.$router.push({name: 'payment_card'})
                }
            else{
                window.open('https://my.cloudpayments.ru/ru/unsubscribe', "_blank")
            }
        },
        logout(){
            axios.get('/logout')
                .then(async resp => {
                    this.setUser("")
                    console.log(this.getUser)
                    this.$router.push({name: 'index'})
                })
        }
    },
}

export default Account_menu
</script>
