					</div>
				</div>
				<div class="list"  :class="{active:editChatStatus}" v-if="this.chatList.length !== 0">
					<div class="list__item" v-for="(chat,index) in this.chatList" @click="checkCheckBox($event, chat.id)">
						<div class="checkBox" >
							<svg width="20" height="21" viewBox="0 0 20 21" fill="none" xmlns="http://www.w3.org/2000/svg">
								<path d="M9.99998 0.704102C4.47715 0.704102 0 5.18125 0 10.7041C0 16.2269 4.47715 20.7041 9.99998 20.7041C15.5228 20.7041 20 16.2269 20 10.7041C19.9941 5.18372 15.5204 0.710003 9.99998 0.704102ZM15.505 7.63766L8.36212 14.7805C8.08317 15.0594 7.63104 15.0594 7.35213 14.7805L4.49497 11.9234C4.21121 11.6493 4.20335 11.1971 4.4774 10.9134C4.75145 10.6296 5.20362 10.6218 5.48738 10.8958C5.49333 10.9016 5.49919 10.9074 5.50496 10.9134L7.85712 13.2655L14.495 6.62767C14.7788 6.35362 15.2309 6.36149 15.505 6.64521C15.7723 6.92202 15.7723 7.36085 15.505 7.63766Z" fill="#4892F3"/>
							</svg>
						</div>
						<div class="avatar">
							<!-- Вставить аватар отправителя -->
							<img v-if="getUserAvatar(chat) != '/avatar.jpg'" :src="getUserAvatar(chat)" alt="">
							<div v-else class="avatar-none"  :class="'bgColor-'+ chat.id % 7">
								{{getSenderName(chat).slice(0,1)}}
							</div>
                            <!-- getOnline -->
                            <svg v-if="getOnline(chat)" class="onlineStatus" width="15" height="15" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <rect x="0.5" y="0.5" width="14" height="14" rx="7" fill="#11D900" stroke="white"/>
                            </svg>
                        </div>
						<a @click="getChatLink(chat)" class="desc">
							<div class="desc__top">
								<!--имя отправителя-->
								<div class="name">
									{{getSenderName(chat)}}
								</div>
								<!--время отправки-->
								<div class="time">{{getMessageTime(chat)}}</div>
							</div>
							<div class="desc__bottom">
								<div class="desc__sms">
									<!--тип сообщения-->
									<div class="sms" v-if="chat.last_message.type == 0">{{chat.last_message.message}}</div>
									<div class="sms" v-else-if="chat.last_message.type  == 1">Фото</div>
									<div class="sms" v-else-if="chat.last_message.type==2">Видео</div>
									<div class="sms" v-else>Счет</div>
								</div>
								<!--кол-во сообщений-->
								<div class="count" v-if="chat.unreads > 0">{{chat.unreads}}</div>
							</div>
						</a>
					</div>
                    <div class="page-account__videoPreloader post_load" v-if="load && !totalLoad"></div>
