<script>
import {mapGetters, mapMutations} from 'vuex'

import {SET_LOAD} from "../store/types";

const Chats = {
	name: "page-massages",
	data: () => ({
		classes:{
			MessageNotificationSwitchOn: false,
			MessageNotificationSwitchOn_content: false,
			DeleteChats: false,
			DeleteChats_content: false,
		},
		user: {
			allow_mass_send: 0,
		},
		load:false,
        totalLoad: false,
		preloader: 0,
		chatList: [],
		filterChats: "",
		chatsToDel: [],
		//chatsFiltered: [],
		editChatStatus:false,
		errors: null,
		file: null,
		type: null,
		price: null,
		messageText: null,
		//showNotify: false,
		toDelete: false,
	}),
    async beforeMount() {
        this.setLoad(true)
        await this.updateChatList();
        this.scroll('scroll');
    },
	computed: {
		...mapGetters([
			'getLoad',
			'getSocket',
			'getUser',
			'getIPhone',
		]),
		countChats(){
			return this.chatList.length >= 20;
		},
		showNotify(){
			return (this.getUser.push_web == 0 || this.getUser.push_telegram == 0) || (this.getUser.push_email == 0 && this.getUser.email.includes('friendsonly.me'))
		}
	},
	methods: {
		...mapMutations({
            setLoad: SET_LOAD,
		}),
		async tgNotify(){
			window.location.href = '/telegram/start'
		},
		filter(){
			if(this.filterChats !== "" && this.filterChats.replace(/\s/g, '').length){
				axios
					.get('/messages/load/chats?search='+ this.filterChats)
					.then(resp => {
                        this.filteredList = resp.data.chats;
						this.chatList = resp.data.chats
					})
					.catch(error => {
						console.log(error)
					})
			} else {
				this.updateChatList()
			}
		},
		switchOffPopup(name, content){
			this.classes[content] = false;
			setTimeout(()=>{
				this.classes[name] = false
			}, 100)
		},
		switchOnPopup(name, content){
			this.classes[name] = true;
			setTimeout(()=>{
				this.classes[content] = true
			}, 100)
		},
		checkUpdate(){
			for(let item of this.chatList){
				//socket place
			}
		},
		clearSearch(){
			this.updateChatList()
			this.filterChats = ""
		},
		checkCheckBox(e, chatId){
			if(this.editChatStatus){
				e.target.classList.toggle('check')
				this.chatsToDel.push(chatId)
			}
		},
		editChat(){
			this.editChatStatus = !this.editChatStatus
		},
		getChatLink(message){
			this.$router.push({name: 'messages', params:{id: message.id}});
		},
		getMessageTime(message){
			let newMessageTime = new Date(message.updated_at)
			let day = new Date().getDate() + "." + (+(new Date().getMonth()) + 1);
			let message_date = newMessageTime.getDate() + "." + (+(newMessageTime.getMonth()) + 1);//newMessageTime.slice(8, 10) + '.' + message.updated_at.slice(5, 7) ;
			if( message_date != day)
				return message_date;
			let time = (+newMessageTime.getMinutes()) >= 10 ? newMessageTime.getMinutes() : ('0' + newMessageTime.getMinutes())
			return newMessageTime.getHours() + ":" + time;
		},
        getOnline(message) {
            let onlineTime = new Date(message.friend.last_online_at),
                nowTime = new Date();
            if ((nowTime - onlineTime) / 1000 < 120) {
                return true;
            } else {
                return false;
            }
        },
		getUserAvatar(message){
			return message.friend.avatar;
		},
		getSenderName(message){
			if (message.friend.username) {
			    return message.friend.username;
            } else if (message.friend.name) {
			    return message.friend.name;
            } else {
			    return message.friend.id;
            }
		},
		deleteChat(){
			for(let chat of this.chatsToDel){
				axios.post(`/messages/delete/chat/${chat}`)
					.then(responce => {
						this.chatList = this.chatList.reduce(function(res, arr){
							if(arr.id != chat){
								res.push(arr)
							}
							return res
						}, [])
						this.chatsToDel = []
					}).catch(function (error) {
					console.log(error)
				})
			}
		},
		updateChatList() {
            axios
                .get('/messages/load/chats')
                .then(response => {
                    if (response.data.chats.length < 20) {
                        this.totalLoad = true;
                    } else {
                        this.totalLoad = false;
                    }
                    this.chatList = response.data.chats;
                    this.setLoad(false);
                })
		},
		onFileChange() {
			let file = this.$refs.file.files[0];
			if (file) {
				this.type = file.type.split("/")[0];
				this.file = file;
			}
		},
		spam(){
			let formt = new FormData();
			if(this.file != null){
				formt.append("type", this.type)
				formt.append("file", this.file)
			}
			if (this.price != null){
				formt.append("price", this.price)
			}
			if(this.file == null && this.price == null){
				formt.append('message', this.messageText);
				this.message_text = null
			}
			axios
				.post("/messages/masssend", formt, {
					headers: {
						"Content-Type": "multipart/form-data"
					}
				})
				.then(response => {
					if(response.status == 200){
						this.message_text = null
						this.cancel()
					}
				})
				.catch(
					function (error) {
					console.log(error)
				})
		},
		cancel(){
			this.price = null;
			this.file = null;
			this.type = null;
		},
		DeclineDel(){
			this.editChatStatus = false;
			this.chatsToDel = [];
		},
		loadMore(){
		    if (!this.totalLoad) {
                this.load = true
                let req;
                if (this.filterChats === "") {
                    req = '/messages/load/chats?last_message_id=' + this.chatList[this.chatList.length - 1].id
                } else {
                    req = `/messages/load/chats?last_message_id=${this.chatList[this.chatList.length - 1].id}&search=${this.filterChats}`
                }
                axios
                    .get(req)
                    .then(response => {
                        console.log(response.data)
                        this.preloader = 2;
                        this.load = false
                        if (this.filterChats === "") {
                            this.chatList = this.chatList.concat(response.data.chats)
                        } else {
                            this.chatList = this.chatList.concat(response.data.chats)
                        }
                        this.preloader = 1;
                        this.checkUpdate();
                        if (response.data.chats.length < 20) {
                            this.totalLoad = true;
                        } else {
                            this.totalLoad = false;
                        }
                    })
                    .catch(function (error) {
                        console.log(error)
                        this.preloader = 2
                    })
            }
		},
        scroll(type) {
		    if (type === 'scroll') {
                const check = () => {
                    let bottomOfWindow = Math.max(window.pageYOffset, document.documentElement.scrollTop, document.body.scrollTop) + window.innerHeight >= document.documentElement.offsetHeight - 75
                    if (bottomOfWindow && !this.totalLoad) {
                        if (!this.load) {
                            this.loadMore();
                        }
                    }
                }
                if (!this.load) {
                    window.ontouch = check
                    window.onscroll = check
                }
            }
        }
	},
}

export default Chats
</script>

